    private void tblFoodMenuMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblFoodMenuMouseClicked
        if(evt.getClickCount() == 2) {
            int row = tblFoodMenu.getSelectedRow();
            int res = javax.swing.JOptionPane.showConfirmDialog(null, "Are you sure you want to delete row " + (tblFoodMenu.getSelectedRow() + 1) + " ?", "Please Confirm", javax.swing.JOptionPane.YES_NO_OPTION);
            switch (res) {
                
                case javax.swing.JOptionPane.YES_OPTION :
                    javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tblFoodMenu.getModel();

                    computePrice(lblTotalFoodPrice, removeComma(String.valueOf(model.getValueAt(row, 4))), "-");
                    computePrice(lblTotalPrice, removeComma(String.valueOf(model.getValueAt(row, 4))), "-");
                    
                    model.removeRow(row);
                    tblFoodMenuLine--;

                    for(int i = row; i < tblFoodMenuLine; i++) {
                        model.setValueAt(i+1, i, 0);
                    }
                    
                    javax.swing.JOptionPane.showMessageDialog(null, "Delete Successfully");
                    break;
                    
                case javax.swing.JOptionPane.NO_OPTION :
                    javax.swing.JOptionPane.showMessageDialog(null, "Delete Action is Canceled");
                    break;
            }   
        }
    }//GEN-LAST:event_tblFoodMenuMouseClicked

