    private void resetToDefault() {
        tableId = customerId = -1;
        tblFoodMenuLine = tblBeverageMenuLine = 0;
        dateTimeReserve = timeOut = null;
        lblTableNo.setText("");
        lblReserve.setText("");
        txtName.setText("");
        deleteAllRow((javax.swing.table.DefaultTableModel) tblFoodMenu.getModel());
        deleteAllRow((javax.swing.table.DefaultTableModel) tblBeverageMenu.getModel());
    }
    
    private void deleteAllRow(javax.swing.table.DefaultTableModel model) {
        for( int i = model.getRowCount() - 1; i >= 0; i-- ) {
            model.removeRow(i);
        }
    }
    
    private void setOrders(int line, javax.swing.JTable tbl) {
        for(int i = 0; i < line; i++) {
            String foodName = String.valueOf(tbl.getValueAt(i, 1));
            int qty = Integer.parseInt(String.valueOf(tbl.getValueAt(i, 3)));
            String reserve = dateTimeReserve.toString().substring(0, dateTimeReserve.toString().length()-2);
            String out = timeOut.toString().substring(0, timeOut.toString().length()-2);
            queue.insertOrders(reserve , out, queue.checkFood(foodName), qty, tableId, customerId);
        }
    }
    
    private void currentTime() {
        javax.swing.Timer timer = new javax.swing.Timer(1000, new java.awt.event.ActionListener() {
           public void actionPerformed(java.awt.event.ActionEvent evt) {
               java.text.DateFormat timeFormat = new java.text.SimpleDateFormat("dd-MM-yyyy   HH:mm:ss");
               java.util.Calendar currentTime = new java.util.GregorianCalendar();
               currentTime.add(java.util.Calendar.YEAR, -543);
               lblClock.setText(timeFormat.format(currentTime.getTime()));

               java.text.DateFormat currentFormat = new java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
               queue.removeOrders(currentFormat.format(currentTime.getTime()));
           } 
        });
        timer.start();
        
    }
    
