    private void addtblFoodMenuLine(javax.swing.JButton btn, double price) {
        javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tblFoodMenu.getModel();
        int i = 0;
        
        for(i = 0; i < tblFoodMenuLine; i++) {
            if(btn.getText().equals(model.getValueAt(i, 1))) {
                model.setValueAt((int)(model.getValueAt(i, 3))+1, i, 3);
                model.setValueAt(putComma(price*removeComma(String.valueOf(model.getValueAt(i, 3)))), i, 4);
                break;
            }
        }
        
        if(tblFoodMenuLine == 0 || i >= tblFoodMenuLine) {
            model.addRow(new Object[0]);
            tblFoodMenu.setValueAt(tblFoodMenuLine + 1, tblFoodMenuLine, 0);
            tblFoodMenu.setValueAt(btn.getText(), tblFoodMenuLine, 1);
            tblFoodMenu.setValueAt(price + "0.-", tblFoodMenuLine, 2);
            tblFoodMenu.setValueAt(1, tblFoodMenuLine, 3);
            tblFoodMenu.setValueAt(price + "0.-", tblFoodMenuLine, 4);
            tblFoodMenuLine++;
        }
        
        computePrice(lblTotalFoodPrice, price, "+");
        computePrice(lblTotalPrice, price, "+");
    }
    
