    private void computePrice(javax.swing.JLabel label, double price, String sign) {
        double totalPrice = 0;
        if(label.getText().equals("")) totalPrice = price;
        else {
            switch(sign) {
                case "+" : totalPrice = removeComma(String.valueOf(label.getText())) + price;
                           break;
                case "-" : totalPrice = removeComma(String.valueOf(label.getText())) - price;  
            }
        }
        if(totalPrice == 0) label.setText("");
        else label.setText(putComma(totalPrice));
    }
    
    private int removeComma(String str) {
        int num = 0;
        try {
            java.text.NumberFormat format = java.text.NumberFormat.getInstance(java.util.Locale.getDefault());
            Number number = format.parse(str);
            num = number.intValue();
        } catch(java.text.ParseException e) {           
        }
        return num;
    }
    
    private String putComma(double num) {
    String s = String.valueOf(num);
    String res = "";
    if(s.length() <= 5) return s + "0.-";
    else {
      int cnt = 0;
      for(int i = s.length()-3; i >= 0; i--) {
        cnt++;
        res = s.charAt(i) + res;
        if(cnt == 3 && i > 0) {
          res = "," + res;
          cnt = 0;
        }
      }
      return res + ".00.-";
    }
  }
    
